import React from "react";

import Task from "./Task";

const Tasks = ({ tasks, handleTaskClick }) => {
  // Componente Tarefas
  return (
    <>
      {tasks.map((task) => (
        <Task task={task} handleTaskClick={handleTaskClick}/>
      ))}
    </>
  );
};

export default Tasks;
